(97, 1, -1, 'Sản Phẩm', 'san-pham', '', '', '', '<p><span style="color:#000000"><span style="font-family:robotoregular; font-size:18px">Với c&aacute;c t&iacute;nh năng mạnh của bộ c&ocirc;ng cụ phần mềm InfosoftBPM như tr&ecirc;n, c&aacute;c chuy&ecirc;n vi&ecirc;n phần mềm c&oacute; thể triển khai c&aacute;c ứng dụng quản l&yacute; v&agrave; triển khai tự động h&oacute;a quy tr&igrave;nh nghiệp vụ quản l&yacute; một c&aacute;ch dễ d&agrave;ng, nhanh ch&oacute;ng, đ&aacute;p ứng linh hoạt theo đặc th&ugrave; quản l&yacute; của c&aacute;c tổ chức, c&ocirc;ng ty hoạt động trong c&aacute;c lĩnh vực ng&agrave;nh nghề kh&aacute;c nhau. B&ecirc;n dưới đ&acirc;y l&agrave; một số ứng dụng cần thiết nhằm n&acirc;ng cao hiệu quả quản l&yacute; điều h&agrave;nh của c&aacute;c tổ chức, c&ocirc;ng ty.</span></span></p>\r\n', '<p>Nội dung</p>\r\n', 'noimg.png', 1, '', 0),
(105, 1, 4, 'Các Dự Án Đã Thực Hiện', 'cac-du-an-da-thuc-hien', '', '', '', '', '', '', 0, '', 0),
(99, 1, 3, 'Giới Thiệu', 'gioi-thieu', 'sd asd', 'gioias as', 'asd a', '<p>Infosoft được th&agrave;nh lập từ năm 2005 với t&ecirc;n gọi đầy đủ l&agrave; C&ocirc;ng ty Cổ phần Phần mềm Th&ocirc;ng tin v&agrave; t&ecirc;n giao dịch l&agrave; Infosoft. Hiện trụ sở ch&iacute;nh của Infosoft đặt tại t/p Hồ Ch&iacute; Minh v&agrave; Chi nh&aacute;nh c&ocirc;ng ty tại H&agrave; Nội.</p>\r\n', '<h2><span style="font-size:16px">Xin ch&acirc;n th&agrave;nh cảm ơn qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; t&iacute;n nhiệm, hợp t&aacute;c v&agrave; ủng hộ Dịch vụ HI Lemon &nbsp;trong thời gian qua. C&ocirc;ng ty ch&uacute;ng t&ocirc;i lu&ocirc;n đặt sự tin cậy của kh&aacute;ch h&agrave;ng l&ecirc;n h&agrave;ng đầu trong qu&aacute; tr&igrave;nh quản l&yacute; v&agrave; đ&agrave;o tạo nguời lao động, để dịch vụ của ch&uacute;ng t&ocirc;i ng&agrave;y c&agrave;ng s&acirc;u rộng v&agrave; chuy&ecirc;n nghiệp hơn về chất lượng c&ocirc;ng việc, phong c&aacute;ch phục vụ lẫn gi&aacute; cả hợp l&yacute;. Nh&agrave; Sạch tự h&agrave;o l&agrave; một trong những c&ocirc;ng ty h&agrave;ng&nbsp;</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:16px">Th&agrave;nh c&ocirc;ng của Hi Lemon h&ocirc;m nay đ&atilde; được x&acirc;y dựng tr&ecirc;n nền văn h&oacute;a doanh nghiệp l&agrave;nh mạnh, nhiệt huyết mang t&iacute;nh to&agrave;n vẹn: Từ t&iacute;nh trung thực, chuy&ecirc;n nghiệp của nh&acirc;n vi&ecirc;n l&agrave;m việc trực tiếp, sự tận t&acirc;m của đội ngũ quản l&yacute; cấp cao&nbsp;</span></p>\r\n\r\n<h1 style="text-align:center"><span style="color:rgb(0, 128, 128)"><span style="font-size:20px"><strong><em>DỊCH VỤ VỆ SINH C&Ocirc;NG NGHIỆP HI LEMON</em></strong></span></span></h1>\r\n\r\n<p style="text-align:center"><span style="color:rgb(0, 128, 128)"><span style="font-size:22px"><strong><em><img alt="điểm chất lượng vệ sinh" src="http://vesinh.net.vn/img_data/images/chat%20luong%20100%25(3).png" style="border:0px; box-sizing:border-box; height:88px; margin:0px; max-width:100%; padding:0px; vertical-align:middle; width:120px" /></em></strong></span></span></p>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><strong><span style="font-size:20px">Tầm Nh&igrave;n - Sứ Mệnh :</span></strong></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">1/ X&acirc;y dựng ho&agrave;n thiện chi nh&aacute;nh 64 tỉnh to&agrave;n quốc .</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">2/Khai th&aacute;c thiết bị hiện đại v&agrave; ph&aacute;t triển c&ocirc;ng nghệ dịch vụ&nbsp;mới .</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">3/&Aacute;p dụng thực hiện nhanh ch&oacute;ng hiệu quả quy tr&igrave;nh ti&ecirc;u chuẩn hạng *.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">4/Ch&uacute; trọng&nbsp; ph&aacute;t triển văn h&oacute;a v&agrave; cơ sở đội ngũ nh&acirc;n lực m&ocirc;i trường l&agrave;m việc chuy&ecirc;n nghiệp .</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">5/Từng bước định h&igrave;nh v&agrave; khẳng định thương hiệu c&ocirc;ng ty ở trong nước v&agrave; ngo&agrave;i nước .</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><strong><span style="font-size:20px">&nbsp;G&iacute;a Trị Cốt L&otilde;i</span></strong></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">1/ Tạo sự tin cậy gắn kết d&agrave;i l&acirc;u.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">2/Kh&ocirc;ng ngừng học hỏi v&agrave; ph&aacute;t triển.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">3/Lu&ocirc;n s&aacute;ng tạo v&agrave; năng động.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">4/Ch&uacute; trọng t&iacute;nh chuy&ecirc;n nghiệp v&agrave; hiệu quả c&ocirc;ng việc.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">5/Đề cao tinh thần tập thể v&agrave; tinh thần tr&aacute;ch nhiệm.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">6/Thỏa m&atilde;n tối đa nhu cầu kh&aacute;ch h&agrave;ng.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><strong><span style="font-size:20px">Cam Kết :</span></strong></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">1/ Chất lượng:</span><span style="font-size:16px">&nbsp;Đảm bảo sự h&agrave;i l&ograve;ng kh&aacute;ch h&agrave;ng 100%.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">2/Tin cậy : Lu&ocirc;n l&agrave; niềm tin cậy với biểu tượng của kh&aacute;ch h&agrave;ng.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">3/Sản phẩm : Đảm bảo lu&ocirc;n ph&aacute;t triển đi kịp xu hướng của kh&aacute;ch h&agrave;ng .</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:16px">4/G&iacute;a trị :G&oacute;i dịch vụ&nbsp;gi&aacute; trị chất lượng hiệu quả v&agrave; gi&aacute; trị cộng th&ecirc;m.</span></span></h6>\r\n\r\n<h6 style="text-align:justify">&nbsp;</h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:20px"><strong>Chiến lược ph&aacute;t triển</strong></span></span></h6>\r\n\r\n<h6 style="text-align:justify">&nbsp;</h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:18px">L&agrave; một trong những doanh nghiệp h&agrave;ng đầu lĩnh vực vệ sinh c&ocirc;ng nghiệp ở Việt Nam, HI LEMON nhận thức r&otilde; tầm ảnh hưởng của m&igrave;nh đến cộng đồng v&agrave; sự ph&aacute;t triển bền vững của x&atilde; hội. Ch&uacute;ng t&ocirc;i cũng thấu hiểu sự th&agrave;nh c&ocirc;ng của một doanh nghiệp sẽ kh&ocirc;ng chỉ đơn giản l&agrave; những con số về doanh thu thể hiện tr&ecirc;n c&aacute;c bản b&aacute;o c&aacute;o thường ni&ecirc;n m&agrave; c&ograve;n l&agrave; những gi&aacute; trị vượt trội v&agrave; l&acirc;u d&agrave;i m&agrave; doanh nghiệp x&acirc;y dựng v&agrave; mang đến cho kh&aacute;ch h&agrave;ng chất lượng dịch vụ tốt nhất.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:18px">Chiến lược ph&aacute;t triển d&agrave;i hạn của HI LEMON l&agrave; đạt mức doanh số để trở th&agrave;nh một trong 3 c&ocirc;ng ty vệ sinh c&ocirc;ng nghiệp lớn nhất Việt nam, với mục ti&ecirc;u trong giai đoạn 2015 - 2020 đạt mức doanh số 45 tỷ VNĐ, mục ti&ecirc;u chiến lược như sau:</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:18px">Kế hoạch đầu tư t&agrave;i sản: Đầu tư thiết bị vệ sinh c&ocirc;ng nghiệp hiện đại v&agrave; đầu tư đ&agrave;o tạo nghiệp vụ kỹ năng v&agrave; t&aacute;c phong đạo đức nh&acirc;n vi&ecirc;n.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:18px">Kh&aacute;ch h&agrave;ng: Mang lại h&agrave;i l&ograve;ng về chất lượng dịch vụ, gi&aacute; cả hợp l&yacute; v&agrave; hệ thống đ&aacute;p ứng c&aacute;c tỉnh th&agrave;nh tại Việt nam.</span></span></h6>\r\n\r\n<h6 style="text-align:justify"><span style="color:rgb(0, 128, 128)"><span style="font-size:18px">Quản trị doanh nghiệp: Trở th&agrave;nh doanh nghiệp c&oacute; cơ cấu, quản trị điều h&agrave;nh chuy&ecirc;n nghiệp được c&ocirc;ng nhận v&agrave; trở th&agrave;nh doanh nghiệp đảm bảo ti&ecirc;u chuẩn iso chất lượng dịch vụ .</span></span></h6>\r\n', 'banner/PROCESOS_3.jpg', 0, '', 0),
